    double findMaxAverage(vector<int>& nums, int k) {
        double sum = 0;
        double maxAvg = numeric_limits<double>::lowest();

        for(int i = 0; i < nums.size(); i++) {

            if(i > k-1) sum -= nums[i-k];

            sum += nums[i];

            double avg = (sum / k);
            if(maxAvg < avg && i >= k-1) maxAvg = avg;
        }

        return maxAvg;
    }
